import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-gatinhos',
  templateUrl: './gatinhos.component.html',
  styleUrls: ['./gatinhos.component.scss']
})
export class GatinhosComponent {
  constructor(private router: Router) { }

  reiniciar(){
    this.router.navigate(['/porta']);

  }
}
<div>
  <button class="reiniciar-btn" (click)="reiniciar()" >Reiniciar</button>
  <img src="assets/gatos.jpg" alt="">
</div>
