import { Component } from '@angular/core';

@Component({
  selector: 'app-porta-cadeados',
  templateUrl: './porta-cadeados.component.html',
  styleUrls: ['./porta-cadeados.component.css']
})
export class PortaCadeadosComponent {
  private cadeadosPequenosAbertos = 0;
  private cadeadoGrandeAberto = false;

  abrirCadeadoPequeno() {
    if (!this.cadeadoGrandeAberto) {
      this.cadeadosPequenosAbertos++;

      if (this.cadeadosPequenosAbertos === 3) {
        this.cadeadoGrandeAberto = true;
      }
    }

    if (this.cadeadoGrandeAberto) {
      // Ação para abrir a porta
      console.log('Parabéns! A porta está aberta!');
    } else {
      console.log('Você ainda precisa abrir todos os cadeados pequenos!');
    }
  }

  abrirCadeadoGrande() {
    if (this.cadeadoGrandeAberto) {
      // Ação para abrir a porta
      console.log('Parabéns! A porta está aberta!');
    } else {
      console.log('Você ainda precisa abrir todos os cadeados pequenos!');
    }
  }
}
<div>
  <h2>Porta dos Cadeados</h2>
  <button (click)="abrirCadeadoPequeno()">Cadeado Pequeno 1</button>
  <button (click)="abrirCadeadoPequeno()">Cadeado Pequeno 2</button>
  <button (click)="abrirCadeadoPequeno()">Cadeado Pequeno 3</button>
  <button (click)="abrirCadeadoGrande()" [disabled]="!cadeadoGrandeAberto">Cadeado Grande</button>
</div>
