@Component({
  selector: 'app-porta-cadeados3',
  templateUrl: './porta-cadeados3.component.html',
  styleUrls: ['./porta-cadeados3.component.scss']
})
export class PortaCadeados3Component {
  cadeadoPequenoUm = false;
  senha: String = "";


  adicionaNumero(){

  }

}
<div>
  <h2>Porta dos Cadeados 3</h2><br>
  <div class="teclado-container">
    <img class="imgCadeadosPequenos" *ngIf="!cadeadoPequenoUm" src="assets/cadeadoFechado.png">
    <img class="imgCadeadosPequenos" *ngIf="cadeadoPequenoUm" src="assets/cadeadoAberto.png">
    <div class="visor">
      {{ senhaVisor }}
    </div>
    <div class="teclado">
      <button class="btn">1</button>
      <button class="btn">2</button>
      <button class="btn">3</button>
      <button class="btn">4</button>
      <button class="btn">5</button>
      <button class="btn">6</button>
      <button class="btn">7</button>
      <button class="btn">8</button>
      <button class="btn">9</button>
      <button class="btn">0</button>
    </div>
  </div>
</div>
