import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-porta-cadeados',
  templateUrl: './porta-cadeados.component.html',
  styleUrls: ['./porta-cadeados.component.scss']
})
export class PortaCadeadosComponent {
  cadeadoPequenoUm = false;
  cadeadoPequenoDois = false;
  cadeadoPequenoTres = false;
  cadeadoGrandeAberto = false;
  totalCadeadoPequenoAberto = 0;

  constructor(private router: Router) { }


  abrirCadeadoPequenoUm() {
    if (!this.cadeadoPequenoUm) {
      this.totalCadeadoPequenoAberto += 1;
    }
    this.cadeadoPequenoUm = true;
  }

  abrirCadeadoPequenoDois() {
    if (!this.cadeadoPequenoDois) {
      this.totalCadeadoPequenoAberto += 1;
    }
    this.cadeadoPequenoDois = true;


  }

  abrirCadeadoPequenoTres() {
    if (!this.cadeadoPequenoTres) {
      this.totalCadeadoPequenoAberto += 1;
    }
    this.cadeadoPequenoTres = true;


  }


  navegarPorta2() {
    this.router.navigate(['/porta2']);
  }

  abrirCadeadoGrande() {
    if (this.cadeadoPequenoUm && this.cadeadoPequenoDois && this.cadeadoPequenoTres) {
      let resposta = confirm("Cadeado grande aberto")
      if(resposta){
        this.navegarPorta2();
      }
    } else {
      alert("Você ainda precisa abrir os cadeados pequenos!")
    }
  }
}
<div>
  <h2>Porta dos Cadeados</h2>
  <button (click)="abrirCadeadoPequenoUm()">Cadeado Pequeno 1</button>
  <button (click)="abrirCadeadoPequenoDois()">Cadeado Pequeno 2</button>
  <button (click)="abrirCadeadoPequenoTres()">Cadeado Pequeno 3</button>
  <button (click)="abrirCadeadoGrande()" [disabled]="!(totalCadeadoPequenoAberto > 2)">Cadeado Grande</button>
</div>
