import { Component } from '@angular/core';

@Component({
  selector: 'app-porta-cadeados3',
  templateUrl: './porta-cadeados3.component.html',
  styleUrls: ['./porta-cadeados3.component.scss']
})
export class PortaCadeados3Component {
  cadeadoPequenoUm = false;
  senha = '';
  senhaVisor = '';
  senhaCorreta = '28091998';


  adicionaNumero(numero: number) {
    if (this.senha.length < 8) {
      this.senha += numero;
      this.senhaVisor += '*';
    }

    if(this.senha.length == 8){
      if (this.senha == this.senhaCorreta) {
        this.cadeadoPequenoUm = true;
      } else {
        this.senha = '';
        this.senhaVisor = '';
        alert('Senha incorreta!');
      }
    }


  }

}
<div>
  <h2>Porta dos Cadeados 3</h2><br>
  <div class="teclado-container">
    <img class="imgCadeadosPequenos" *ngIf="!cadeadoPequenoUm" src="assets/cadeadoFechado.png">
    <img class="imgCadeadosPequenos" *ngIf="cadeadoPequenoUm" src="assets/cadeadoAberto.png">

    <div class="visor">
      {{ senhaVisor }}
    </div>
    <div class="teclado">
      <button class="btn" (click)="adicionaNumero(1)">1</button>
      <button class="btn" (click)="adicionaNumero(2)">2</button>
      <button class="btn" (click)="adicionaNumero(3)">3</button>
      <button class="btn" (click)="adicionaNumero(4)">4</button>
      <button class="btn" (click)="adicionaNumero(5)">5</button>
      <button class="btn" (click)="adicionaNumero(6)">6</button>
      <button class="btn" (click)="adicionaNumero(7)">7</button>
      <button class="btn" (click)="adicionaNumero(8)">8</button>
      <button class="btn" (click)="adicionaNumero(9)">9</button>
      <button class="btn" (click)="adicionaNumero(0)">0</button>
    </div>
  </div>
</div>
