import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-porta-cadeados2',
  templateUrl: './porta-cadeados2.component.html',
  styleUrls: ['./porta-cadeados2.component.scss']
})
export class PortaCadeados2Component {
  cadeadoPequenoUm = false;
  cadeadoPequenoDois = false;
  cadeadoPequenoTres = false;
  cadeadoPequenoQuatro = false;
  cadeadoGrandePrincipal = false;
  totalCadeadoPequenoAberto = 0;

  constructor(private router: Router) { }


  abrirCadeadoPequenoUm() {
    if (!this.cadeadoPequenoUm) {
      this.totalCadeadoPequenoAberto += 1;
    }
    this.cadeadoPequenoUm = true;
  }

  abrirCadeadoPequenoDois() {
    if (!this.cadeadoPequenoDois) {
      this.totalCadeadoPequenoAberto += 1;
    }
    this.cadeadoPequenoDois = true;


  }

  abrirCadeadoPequenoTres() {
    if (!this.cadeadoPequenoTres) {
      this.totalCadeadoPequenoAberto += 1;
    }
    this.cadeadoPequenoTres = true;


  }

  abrirCadeadoPequenoQuatro() {
    if (!this.cadeadoPequenoQuatro) {
      this.totalCadeadoPequenoAberto += 1;
    }
    this.cadeadoPequenoQuatro = true;


  }


  navegarPorta3() {
    this.router.navigate(['/porta3']);
  }

  abrirCadeadoPrincipal() {
    if (this.cadeadoPequenoUm && this.cadeadoPequenoDois && this.cadeadoPequenoTres) {
      let resposta = confirm("Cadeado grande aberto! Ir para porta 2?")
      if(resposta){
        this.navegarPorta3();
      }
    } else {
      alert("Você ainda precisa abrir os cadeados pequenos!")
    }
  }
}
<div>
  <h2>Porta dos Cadeados 2</h2>
  <img class="imgCadeadosPequenos" *ngIf="!cadeadoPequenoUm" src="assets/cadeadoFechado.png" (click)="abrirCadeadoPequenoUm()">
  <img class="imgCadeadosPequenos" *ngIf="cadeadoPequenoUm" src="assets/cadeadoAberto.png">
