import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-porta-cadeados',
  templateUrl: './porta-cadeados.component.html',
  styleUrls: ['./porta-cadeados.component.scss']
})
export class PortaCadeadosComponent {
  cadeadoPequenoUm = false;
  cadeadoPequenoDois = false;
  cadeadoPequenoTres = false;
  cadeadoGrandeAberto = false;
  totalCadeadoPequenoAberto = 0;

  constructor(private router: Router) { }


  abrirCadeadoPequenoUm() {
    if (!this.cadeadoPequenoUm) {
      this.totalCadeadoPequenoAberto += 1;
    }
    this.cadeadoPequenoUm = true;
  }

  abrirCadeadoPequenoDois() {
    if (!this.cadeadoPequenoDois) {
      this.totalCadeadoPequenoAberto += 1;
    }
    this.cadeadoPequenoDois = true;


  }

  abrirCadeadoPequenoTres() {
    if (!this.cadeadoPequenoTres) {
      this.totalCadeadoPequenoAberto += 1;
    }
    this.cadeadoPequenoTres = true;


  }


  navegarPorta2() {
    this.router.navigate(['/porta2']);
  }

  abrirCadeadoGrande() {
    if (this.cadeadoPequenoUm && this.cadeadoPequenoDois && this.cadeadoPequenoTres) {
      let resposta = confirm("Cadeado grande aberto! Ir para porta 2?")
      if(resposta){
        this.navegarPorta2();
      }
    } else {
      alert("Você ainda precisa abrir os cadeados pequenos!")
    }
  }
}
<div>
  <h2>Porta dos Cadeados 1</h2>
  <div class="posicao-cadeado">

    <img class="imgCadeadosPequenos" *ngIf="!cadeadoPequenoUm" src="assets/cadeadoFechado.png" (click)="abrirCadeadoPequenoUm()">
    <img class="imgCadeadosPequenos" *ngIf="cadeadoPequenoUm" src="assets/cadeadoAberto.png">

    <img class="imgCadeadosPequenos" *ngIf="!cadeadoPequenoDois" src="assets/cadeadoFechado.png" (click)="abrirCadeadoPequenoUm()">
    <img class="imgCadeadosPequenos" *ngIf="cadeadoPequenoDois" src="assets/cadeadoAberto.png">

    <img class="imgCadeadosPequenos" *ngIf="!cadeadoPequenoTre" src="assets/cadeadoFechado.png" (click)="abrirCadeadoPequenoUm()">
    <img class="imgCadeadosPequenos" *ngIf="cadeadoPequenoTre" src="assets/cadeadoAberto.png">


    <img class="imgCadeadosGrandes" src="assets/cadeadoFechado.png" (click)="abrirCadeadoGrande()">


  </div>

</div>
